class Solution {
public:
	int maximalRectangle(vector<vector<char>>& matrix) {
		int max_area = 0;

		vector<vector<int>> matrix_int(matrix.size(), vector<int>(matrix[0].size(),0));

		for (int i = 0; i < matrix.size(); i++)
		{
			for (int j = 0; j < matrix[i].size(); j++)
			{
				if (j == 0)
					matrix_int[i][j] = abs('0' - matrix[i][j]);
				else
				{
					if (matrix[i][j] != '0')
						matrix_int[i][j] = matrix_int[i][j - 1] + abs('0' - matrix[i][j]);
				}
				
				printf("%02d,", matrix_int[i][j]);
			}
			printf("-\n");
		}

		for (int j = 0; j < matrix_int[0].size(); j++)
		{
			monotonic_stack_interface::mono_stack stack_data;
			for (int i = 0; i < matrix_int.size(); i++)
			{
				int back_count = 0;
				while (!stack_data.empty() && matrix_int[i][j] < stack_data.top().second.value)
				{
					back_count = stack_data.top().second.back_data + 1;
					int area_c = (i - stack_data.top().first + 1) * matrix_int[i][j];
					int area_f =  (i - stack_data.top().first + stack_data.top().second.back_data) * stack_data.top().second.value;

					if (max_area < max(area_c, area_f))
						max_area = max(area_c, area_f);
					stack_data.pop();
				}
				stack_data.push({ i,{matrix_int[i][j],back_count} });
			}

			int max_index = stack_data.top().first + 1;
			while (!stack_data.empty())
			{
				int area_f = (max_index - stack_data.top().first + stack_data.top().second.back_data) * stack_data.top().second.value;

				if (max_area < area_f)
					max_area = area_f;
				stack_data.pop();
			}
		}
		return max_area;
	}
};

